import { Injectable } from "@angular/core";



// @Injectable
export class UserService{

  const mysql = require('mysql2');

    var con = mysql.createConnection({
      host: "localhost",
      port: 3306,
      user: "root",
      password: "root",
      database: "users"
    });
    
    con.connect(function(err) {
      if (err) throw err;
      con.query("SELECT * FROM usersinfo", function (err, result, fields) {
        if (err) throw err;
        console.log(result);
      });
    });




    
}
